mapper.select:
select *
from ${baseName}.plate_calc_house_rank
where plate_id = #{plateId,jdbcType=INTEGER}
  and house_id = #{houseId,jdbcType=INTEGER}
  and months = #{months,jdbcType=SMALLINT}
  and valyear = #{valyear,jdbcType=SMALLINT}
  and valmonth = #{valmonth,jdbcType=SMALLINT}

mapper.delete:
delete from ${baseName}.plate_calc_house_rank
where plate_id = #{plateId,jdbcType=INTEGER}
  and house_id = #{houseId,jdbcType=INTEGER}
  and months = #{months,jdbcType=SMALLINT}
  and valyear = #{valyear,jdbcType=SMALLINT}
  and valmonth = #{valmonth,jdbcType=SMALLINT}

mapper.update:
update ${baseName}.plate_calc_house_rank


    avprice = #{avprice,jdbcType=INTEGER},


    rise = #{rise,jdbcType=INTEGER},


    calc_time = #{calcTime,jdbcType=TIMESTAMP},


where plate_id = #{plateId,jdbcType=INTEGER}
  and house_id = #{houseId,jdbcType=INTEGER}
  and months = #{months,jdbcType=SMALLINT}
  and valyear = #{valyear,jdbcType=SMALLINT}
  and valmonth = #{valmonth,jdbcType=SMALLINT}

update ${baseName}.plate_calc_house_rank
set avprice = #{avprice,jdbcType=INTEGER},
  rise = #{rise,jdbcType=INTEGER},
  calc_time = #{calcTime,jdbcType=TIMESTAMP}
where plate_id = #{plateId,jdbcType=INTEGER}
  and house_id = #{houseId,jdbcType=INTEGER}
  and months = #{months,jdbcType=SMALLINT}
  and valyear = #{valyear,jdbcType=SMALLINT}
  and valmonth = #{valmonth,jdbcType=SMALLINT}

mapper.select:
select *
from ${baseName}.plate_calc_house_rank
where 1 = 1 

    and plate_id = #{plateId,jdbcType=INTEGER}


    and house_id = #{houseId,jdbcType=INTEGER}


    and months = #{months,jdbcType=SMALLINT}


    and valyear = #{valyear,jdbcType=SMALLINT}


    and valmonth = #{valmonth,jdbcType=SMALLINT}


    and avprice = #{avprice,jdbcType=INTEGER}


    and rise = #{rise,jdbcType=INTEGER}


    and calc_time = #{calcTime,jdbcType=TIMESTAMP}

  			SELECT price_rate AS priceRate FROM ${baseName}.house_trending WHERE valyear=#{valyear,jdbcType=VARCHAR}
			AND valmonth = #{valmonth,jdbcType=INTEGER}
			AND house_id = #{houseId,jdbcType=INTEGER}
			AND price_month= #{priceMonth,jdbcType=INTEGER}

  			SELECT sales_rate AS salesRate FROM ${baseName}.house_trending WHERE valyear=#{valyear,jdbcType=VARCHAR}
			AND valmonth = #{valmonth,jdbcType=INTEGER}
			AND house_id = #{houseId,jdbcType=INTEGER}
			AND sales_month = #{salesMonth,jdbcType=INTEGER}

  			SELECT trending_result AS trendResult FROM ${baseName}.house_trending WHERE valyear=#{valyear,jdbcType=VARCHAR}
			AND valmonth = #{valmonth,jdbcType=INTEGER}
			AND house_id = #{houseId,jdbcType=INTEGER}
			AND price_month= #{priceMonth,jdbcType=INTEGER}

  			SELECT house_id AS houseId,price_rate AS priceRate, sales_rate AS salesRate,trending_result AS trendingResult FROM ${baseName}.house_trending WHERE valyear=#{valyear,jdbcType=VARCHAR}
			AND valmonth = #{valmonth,jdbcType=INTEGER}
			AND house_id = #{houseId,jdbcType=INTEGER}
